const { applyOperations } = require('staffbotics-helpers');

function safeParseJsonArray(text) {
  if (!text || typeof text !== 'string') return [];
  try {
    const parsed = JSON.parse(text);
    return Array.isArray(parsed) ? parsed : [];
  } catch (e) {}
  const m = text.match(/\[[\s\S]*\]/);
  if (m) {
    try {
      const parsed = JSON.parse(m[0]);
      return Array.isArray(parsed) ? parsed : [];
    } catch (e) {}
  }
  return [];
}

// Reorg webhook payload (the state the user sends in)
const payload = $node['Apply Reorg (Webhook)'].json.body ?? {};
const patientCandidates = payload.patientCandidates ?? [];
const rawItems = payload.rawItems ?? [];
const userInstruction = payload.instruction ?? '';

// LLM operations response content
const llmResp = items[0].json;
const content = llmResp?.choices?.[0]?.message?.content ?? '';
const operations = safeParseJsonArray(content);

// Build lookup for rawItems to repair reassign stubs
const rawItemById = {};
for (const ri of rawItems) rawItemById[ri.id] = ri;

const out = applyOperations(patientCandidates, operations, userInstruction);

// Repair stubs produced by applyOperations(reassign)
for (const pc of out.patientCandidates || []) {
  pc.raw_items = (pc.raw_items || []).map(ri => {
    if (ri && ri.source_ref === 'reassigned' && rawItemById[ri.id]) return rawItemById[ri.id];
    return ri;
  });
}

return [{
  json: {
    patientCandidates: out.patientCandidates,
    operationsApplied: out.operationsApplied,
    userInstruction,
    rawItems,
    llm_raw: content
  }
}];
